const observerOptions={threshold: 0.1,rootMargin: '0px 0px -50px 0px'};const observer=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if (entry.isIntersecting){entry.target.classList.add('animate');observer.unobserve(entry.target);}});},observerOptions);function initScrollAnimations(){const animatedElements=document.querySelectorAll('.animate-on-scroll');animatedElements.forEach(element=>{observer.observe(element);});}if (document.readyState==='loading'){document.addEventListener('DOMContentLoaded',initScrollAnimations);}else{initScrollAnimations();}document.documentElement.style.scrollBehavior='smooth';window.addEventListener('scroll',()=>{const scrolled=window.pageYOffset;const parallaxElements=document.querySelectorAll('.parallax');parallaxElements.forEach(element=>{const speed=element.dataset.speed || 0.5;element.style.transform=`translateY(${scrolled * speed}px)`;});});