document.addEventListener('DOMContentLoaded',function(){const dropdown=document.querySelector('.dropdown');const hamburger=document.getElementById('hamburger');const menu=document.getElementById('menu');let timeoutId;dropdown.addEventListener('click',function(e){e.preventDefault();if (timeoutId){clearTimeout(timeoutId);}dropdown.classList.add('active');timeoutId=setTimeout(function(){dropdown.classList.remove('active');},3000);});document.addEventListener('click',function(e){if (!dropdown.contains(e.target)){dropdown.classList.remove('active');if (timeoutId){clearTimeout(timeoutId);}}});hamburger.addEventListener('click',function(){hamburger.classList.toggle('active');menu.classList.toggle('active');});const menuItems=menu.querySelectorAll('li a');menuItems.forEach(function(item){item.addEventListener('click',function(){hamburger.classList.remove('active');menu.classList.remove('active');});});document.addEventListener('click',function(e){if (!hamburger.contains(e.target) && !menu.contains(e.target)){hamburger.classList.remove('active');menu.classList.remove('active');}});});