document.addEventListener('DOMContentLoaded',function(){initializeAnimations();initializeServiceCards();initializePricingCards();initializeProcessSteps();initializeSmoothScrolling();});function initializeAnimations(){const observerOptions={threshold: 0.1,rootMargin: '0px 0px -50px 0px'};const observer=new IntersectionObserver(function(entries){entries.forEach(entry=>{if (entry.isIntersecting){entry.target.classList.add('visible');}});},observerOptions);const animatedElements=[{selector: '.service-card',animation: 'fade-in'},{selector: '.service-detail:nth-child(odd) .detail-content',animation: 'slide-in-left'},{selector: '.service-detail:nth-child(even) .detail-content',animation: 'slide-in-right'},{selector: '.process-step',animation: 'fade-in'},{selector: '.pricing-card',animation: 'fade-in'}];animatedElements.forEach(item=>{const elements=document.querySelectorAll(item.selector);elements.forEach((element,index)=>{element.classList.add(item.animation);element.style.transitionDelay=`${index * 0.1}s`;observer.observe(element);});});}function initializeServiceCards(){const serviceCards=document.querySelectorAll('.service-card');serviceCards.forEach(card=>{card.addEventListener('mouseenter',function(){this.style.transform='translateY(-10px) scale(1.02)';});card.addEventListener('mouseleave',function(){this.style.transform='translateY(0) scale(1)';});card.addEventListener('click',function(){const serviceName=this.querySelector('h4').textContent;showServiceModal(serviceName);});});}function initializePricingCards(){const pricingCards=document.querySelectorAll('.pricing-card');pricingCards.forEach(card=>{card.addEventListener('mouseenter',function(){if (!this.classList.contains('featured')){this.style.transform='translateY(-10px) scale(1.02)';}else{this.style.transform='scale(1.05) translateY(-15px)';}});card.addEventListener('mouseleave',function(){if (!this.classList.contains('featured')){this.style.transform='translateY(0) scale(1)';}else{this.style.transform='scale(1.05) translateY(0)';}});});}function initializeProcessSteps(){const processSteps=document.querySelectorAll('.process-step');processSteps.forEach((step,index)=>{step.addEventListener('mouseenter',function(){const stepIcon=this.querySelector('.step-icon');const stepNumber=this.querySelector('.step-number');stepIcon.style.transform='scale(1.1) rotate(5deg)';stepNumber.style.transform='scale(1.1)';});step.addEventListener('mouseleave',function(){const stepIcon=this.querySelector('.step-icon');const stepNumber=this.querySelector('.step-number');stepIcon.style.transform='scale(1) rotate(0deg)';stepNumber.style.transform='scale(1)';});});}function initializeSmoothScrolling(){const links=document.querySelectorAll('a[href^="#"]');links.forEach(link=>{link.addEventListener('click',function(e){e.preventDefault();const targetId=this.getAttribute('href').substring(1);const targetElement=document.getElementById(targetId);if (targetElement){targetElement.scrollIntoView({behavior: 'smooth',block: 'start'});}});});}function showServiceModal(serviceName){const serviceInfo={'Web Development':{description: 'Custom websites and web applications built with modern technologies and best practices.',features: ['Responsive Design','E-commerce Solutions','CMS Development','API Integration'],timeline: '2-8 weeks',price: 'Starting at $1,500'},'Digital Marketing':{description: 'Comprehensive digital marketing strategies to boost your online presence and drive results.',features: ['SEO Optimization','PPC Campaigns','Email Marketing','Analytics & Reporting'],timeline: 'Ongoing monthly service',price: '$800-$3,000/month'},'Brand Strategy':{description: 'Complete brand identity and strategy development for your business.',features: ['Logo Design','Brand Guidelines','Market Research','Brand Positioning'],timeline: '3-6 weeks',price: '$2,000-$5,000'},'Social Media Management':{description: 'Professional social media management to engage your audience and build your brand.',features: ['Content Creation','Community Management','Social Advertising','Performance Analytics'],timeline: 'Ongoing monthly service',price: '$600-$2,000/month'},'Content Creation':{description: 'High-quality content that resonates with your target audience and drives engagement.',features: ['Blog Writing','Video Production','Graphic Design','Copywriting'],timeline: 'Ongoing monthly service',price: '$500-$1,500/month'},'Business Analytics':{description: 'Data-driven insights to optimize your business performance and make informed decisions.',features: ['Performance Tracking','Custom Dashboards','ROI Analysis','Strategic Recommendations'],timeline: 'Ongoing monthly service',price: '$1,000-$3,000/month'}};const service=serviceInfo[serviceName];if (service){const modalHTML=` <div class="modal fade" id="serviceModal" tabindex="-1"> <div class="modal-dialog modal-lg"> <div class="modal-content"> <div class="modal-header"> <h5 class="modal-title">${serviceName}</h5> <button type="button" class="btn-close" data-bs-dismiss="modal"></button> </div> <div class="modal-body"> <p class="lead">${service.description}</p> <div class="row"> <div class="col-md-6"> <h6>Key Features:</h6> <ul> ${service.features.map(feature=> `<li>${feature}</li>`).join('')}</ul> </div> <div class="col-md-6"> <h6>Timeline:</h6> <p>${service.timeline}</p> <h6>Investment:</h6> <p class="text-primary fw-bold">${service.price}</p> </div> </div> </div> <div class="modal-footer"> <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button> <a href="enquiry.html" class="btn btn-primary">Get Quote</a> <a href="booking.html" class="btn btn-outline-primary">Book Consultation</a> </div> </div> </div> </div> `;const existingModal=document.getElementById('serviceModal');if (existingModal){existingModal.remove();}document.body.insertAdjacentHTML('beforeend',modalHTML);const modal=new bootstrap.Modal(document.getElementById('serviceModal'));modal.show();}}function animateCounters(){const counters=document.querySelectorAll('.counter');counters.forEach(counter=>{const target=parseInt(counter.getAttribute('data-target'));const duration=2000;const increment=target / (duration / 16);let current=0;const updateCounter=()=>{current +=increment;if (current < target){counter.textContent=Math.floor(current);requestAnimationFrame(updateCounter);}else{counter.textContent=target;}};updateCounter();});}function initializeParallax(){window.addEventListener('scroll',function(){const scrolled=window.pageYOffset;const heroIcon=document.querySelector('.hero-icon');if (heroIcon){const speed=scrolled * 0.5;heroIcon.style.transform=`translateY(${speed}px)`;}});}if (document.querySelector('.services-hero')){initializeParallax();}function initializeServiceFilter(){const filterButtons=document.querySelectorAll('.filter-btn');const serviceCards=document.querySelectorAll('.service-card');filterButtons.forEach(button=>{button.addEventListener('click',function(){const filter=this.getAttribute('data-filter');filterButtons.forEach(btn=> btn.classList.remove('active'));this.classList.add('active');serviceCards.forEach(card=>{if (filter==='all' || card.classList.contains(filter)){card.style.display='block';card.style.animation='fadeIn 0.5s ease';}else{card.style.display='none';}});});});}function initializeTestimonialSlider(){const testimonials=document.querySelectorAll('.testimonial');let currentTestimonial=0;if (testimonials.length > 0){setInterval(()=>{testimonials[currentTestimonial].classList.remove('active');currentTestimonial=(currentTestimonial + 1) % testimonials.length;testimonials[currentTestimonial].classList.add('active');},5000);}}function initializeFAQ(){const faqItems=document.querySelectorAll('.faq-item');faqItems.forEach(item=>{const question=item.querySelector('.faq-question');const answer=item.querySelector('.faq-answer');question.addEventListener('click',function(){const isActive=item.classList.contains('active');faqItems.forEach(faq=>{faq.classList.remove('active');faq.querySelector('.faq-answer').style.maxHeight='0';});if (!isActive){item.classList.add('active');answer.style.maxHeight=answer.scrollHeight + 'px';}});});}if (typeof SwiftChatbot !=='undefined'){const chatbot=new SwiftChatbot();chatbot.init();}window.ServicesPage={showServiceModal,animateCounters,initializeServiceFilter,initializeTestimonialSlider,initializeFAQ};